<script>
    import uniNumberBox from '@/components/uni-number-box.vue';
	import API from '@/common/API.js';
	export default {
        components: {
        	uniNumberBox
        },
		data() {
			return {
				shoppingCartID: '', //购物车id
				goodsID: "", //商品id
				preferentialMoney: 0, //优惠金额
				goodsMoney: 0, //商品金额
				payment: 0, //实际付款
				dataList: [],
				imgurl: API.config.imgUrl,
				// fors:[{
				// 	goodsName:'商品名称1',
				// 	goodsType:'商品类型1',
				// 	goodsPrice:'商品价格1',
				// 	goodsNum:'商品数量1',
				// 	goodsImg:'https://ss2.bdstatic.com/70cFvnSh_Q1YnxGkpoWK1HF6hhy/it/u=756705744,3505936868&fm=11&gp=0.jpg',
				// },{
				// 	goodsName:'商品名称2',
				// 	goodsType:'商品类型2',
				// 	goodsPrice:'商品价格2',
				// 	goodsNum:'商品数量2',
				// 	goodsImg:'https://ss0.bdstatic.com/70cFvHSh_Q1YnxGkpoWK1HF6hhy/it/u=1620020012,789258862&fm=26&gp=0.jpg',
				// }],
				//商家图标
				merchantsImg: 'http://duoduo.qibukj.cn/./Upload/Images/20190321/201903211727515.png',
				merchants: "店铺名称", //商家
				maskState: 0, //优惠券面板显示状态
				desc: '', //备注
				payType: 1, //1微信 2支付宝
				couponList: [{
						title: '新用户专享优惠券',
						price: 5,
					},
					{
						title: '庆五一发一波优惠券',
						price: 10,
					},
					{
						title: '优惠券优惠券优惠券优惠券',
						price: 15,
					}
				],
				addressData: [],
				isShop: false
				// addressData: {
				// 	name: '许小星',
				// 	mobile: '13853989563',
				// 	addressName: '金九大道',
				// 	address: '山东省济南市历城区',
				// 	area: '149号',
				// 	default: false,
				// }
			}
		},
		created() {

		},
		onLoad(option) {
			console.log('option', option)
			let member_id = uni.getStorageSync('CLOUDIN_WLF_MEMBER_ID')
			//请求地址
			API._GET('/api/memberaddress/default/me', {
				member_id: member_id
			}, res => {
				console.log(res)
				if (res.code == 0) {
					this.addressData = res.data
				}
			})
			
			console.log('页面进入onload',JSON.stringify(this.dataList))
			if (option.status && option.status == "shop") {
				//从购物车进入创建订单
				this.isShop = true
				//请求我的商品列表
				let params = {
					page: 1,
					limit: 10 * option.page,
					member_id: member_id
				}
				let arr = JSON.parse(option.data)
				console.log('arr',arr.goodsData)
				// for(var i = 0; i < arr.goodsData.length; i++) {
				// 	this.goodsMoney +=	arr.goodsData[i].price * arr.goodsData[i].number
				// }
				// this.payment = this.goodsMoney
				let arrs = []
				this.goodsMoney = 0
				API._GET('/api/cart/me', params, res => {
					console.log(res)
					if (res.code == 0) {
						let arrID = []
						for (var i = 0; i < res.data.length; i++) {
							for(var a = 0; a < arr.goodsData.length; a++){
								if (arr.goodsData[a].id === res.data[i].cart_id) {
									let item = res.data[i]
									console.log(res.data[i])
									res.data[i]['goodsAmounts'] = arr.goodsData[a].number
									arrID.push(res.data[i].cart_id)
									this.dataList.push(res.data[i])
									this.goodsMoney = this.goodsMoney + item.attr_price * item.goodsAmounts
									this.payment = this.goodsMoney
								}
							}
							// this.goodsMoney = ((this.goodsMoney * 100) + (res.data[i].goods_price * 100)) / 100
						}
						
						this.shoppingCartID = arrID.join(",")
						console.log(this.dataList)
					}
				})
			} else {
				this.isShop = false
				//从立即购买进入创建订单
				this.goodsID = option.goodsID
				// this.goodsMoney = option.goodsMoney
				// this.payment = ((Number(this.goodsMoney) * 100) - (Number(this.preferentialMoney) * 100)) / 100
				this.shoppingCartID = option.shoppingCartID
				//请求 商品列表
				API._GET_ID('/api/cart', option.shoppingCartID, res => {
					console.log(res)
					if (res.code == 0) {
						this.goodsMoney = res.data.attr_price?res.data.attr_price:res.data.goods_price
						this.payment = this.goodsMoney
						res.data['goodsAmounts'] = 1
						this.dataList.push(res.data)
						console.log('res.data', res.data);
					}
				})
			}
			//商品数据
			//let data = JSON.parse(option.data);
			//console.log(data);
		},
		onShow() {
			console.log('进入onshow了')
		},
		methods: {
			//显示优惠券面板
			toggleMask(type) {
				let timer = type === 'show' ? 10 : 300;
				let state = type === 'show' ? 1 : 0;
				this.maskState = 2;
				setTimeout(() => {
					this.maskState = state;
				}, timer)
			},
			numberChange(data) {
                console.log(data)
                let cart_id = this.dataList[data.index].cart_id
				this.number = data.number;
                API._POST(`/api/cart/amount?card_id=${cart_id}&amount=${data.number}`,{}, res => {
                  console.log(res)
                  if (res.code == 0) {
                    this.calcTotal();
                  }
                })
                this.dataList[data.index].goodsAmounts = data.number;
			},
			calcTotal() {
				this.goodsMoney = 0
				for (var i = 0; i < this.dataList.length; i++) {
					let item = this.dataList[i]
					this.goodsMoney += item.attr_price * item.goodsAmounts
					this.payment = this.goodsMoney
				}
			},
			changePayType(type) {
				this.payType = type;
			},
			//点击下单按钮
			submit() {
				// 'https://api.wanlef.com/api/orders/overbooking'
				//请求订单数据/api/orders/overbooking
				console.log('addressData',JSON.stringify(this.addressData))
				if (!this.addressData) {
					uni.showToast({
						title: '请选择地址!',
						icon: "none"
					});
					return
				}
				let memberID = uni.getStorageSync('CLOUDIN_WLF_MEMBER_ID')
				let dataObj = JSON.stringify([{
					goods_id: this.goodsID,
					// goods_amout:
				}])

				let _this = this
				let params = {
					order_no: String(this.shoppingCartID), //购物车id
					member_id: memberID, //当前登录id
					// total_price: 0,
					// off_price: 0,
					goods_amount: 1,
					order_status: 1,
					pay_status: 1,
					is_card: false, ////是否使用礼品卡
					// card_no: "string",
					province_id: this.addressData ? this.addressData.province_id : 0, //省份id
					city_id: this.addressData ? this.addressData.city_id : 0, //市id
					district_id: this.addressData ? this.addressData.district_id : 0, //区id
					member_name: this.addressData?this.addressData.member_name: '', //收件人姓名
					member_address: this.addressData?this.addressData.address_detail:'', //收件人地址
					member_phone: this.addressData?this.addressData.mobile_phone:'', //收件人手机
					platform_id: 4, //来源终端id
					goods_attr: '',
					order_pay_no: this.desc
				}
				API._POST('/api/orders/overbooking', params, res => {
					console.log(res)
					if (res.code == 0) {
						uni.showToast({
							title: '下单成功'
						});
						console.log(res.data)
						this.gotoPay(res.data);
					}
				})

			},
			//确认支付
			gotoPay(item){
				uni.redirectTo({
					url: '/pages/money/pay?money=' + item.total_price + '&orderID=' + item.order_id
				})
			},
			stopPrevent() {}
		}
	}
</script>

